            if(subCount>k){
                ans = (ans*fastPow(curMax, k))%mod;
                // System.out.println("乘以"+curMax+"的"+k+"次方");
                k = 0;
            }else{
                ans = (ans*fastPow(curMax, subCount))%mod;
                // System.out.println("乘以"+curMax+"的"+subCount+"次方");
                k -= subCount;
            }
        }
        return (int)ans;
    }

    public long fastPow(long x,int y){
        long ans = 1;
        while(y>0){
            if((y&1)==1){
                ans = (x*ans)%mod;
            }
            x = (x*x)%mod;
            y >>= 1;
        }
        return ans;
    }
}
```

